if (datasource.ApiToken !== undefined && datasource.ApiToken != '') {
    callNoteBook()
} else {
    datasource.ApiToken = ''
    const config = {
        url: prism.proxyurl + '/api/v1/authentication/tokens/api',
        method: 'GET',
        contentType: 'application/json',
        async: false,
    }

    res = $internalHttp(config, false)
    res.then(function (response) {
        datasource.ApiToken = response.data.token
        callNoteBook()
    })
}
